  }

  async function getProfessionsList() {
    try {
      const { content } = await professionService.get();
      setProfessions(content);
      setLoading(false);
    } catch (error) {
      errorCatcher(error);
    }
  }

  return (
    <ProfessionContext.Provider value={{ isLoading, professions }}>
      {children}
    </ProfessionContext.Provider>
  );
};
